$(document).ready(function() {

    ajax('GET', '/api/course', {}, function (a) {
        $('#des').empty();
        $('#pics').empty();
        if (a.data)
        {
            a.data.content = a.data.content.replaceAll('</p>nn<p>', '</p><p>');
            $('#des').html(a.data.content.substring(1, a.data.content.length-2));
            $('#pics').empty();
            for (let i in a.data.images)
            {
                let div = '<div class="d-flex -justify-content-center align-items-center">'+ 
                            '<img src="/upload/Image/'+ a.data.images[i].uuid_name+'" />' +
                          '</div>';
                $('#pics').append(div);
            }
        }
    })

 });