$(document).ready(function() {

    $('#breadcrumb').hide();

    const queryString = window.location.search;
    const urlParams = new URLSearchParams(queryString);
    const tid = urlParams.get('tid')

    ajax('GET', '/api/news/'+tid, {}, function (a) {

        $('#des').html(a.data.content.substring(1, a.data.content.length-2));
        $('#category').text((a.data.category == 1) ? 'News' : 'Blogs');
        $('#title').text(a.data.title);
        $('#link').text((a.data.link_alt) ? a.data.link_alt : 'Read More');
        $("#link").on("click", function(){ window.open(a.data.link, '_blank'); });
        $('#created_at').text(a.data.created_at);
        $('#updated_at').text(a.data.updated_at);
        
        let response = '';
        response += 
        '<div class="parent">\
            <img class="owl-img" src="upload/Image/' + a.data.image + '" >\
        </div>';

        for (let i = 0; i < a.data.images; i++) {
            let img = a.data[i].image;
            // let link = (a.data[i].link) ? 'onclick=location.href="'+a.data[i].link+'" target="_blank"' : '';
            response += '<div class="parent" >\
                            <img class="owl-img" src="upload/Image/' + img[0].uuid_name + '" >\
                        </div>';
        }
        $('.owl-carousel').append(response);

        $('.owl-carousel').owlCarousel({
            loop:true,
            margin:10,
            nav:false,
            items: 1,
            dots: false,
            autoHeight:true,
            autoplay:true,
            autoplayTimeout:3000,
            autoplayHoverPause:true
        })


        console.log(a.data);
    });
 });