$(document).ready(function() {


    // <div class="col-12 col-lg-5" style="padding: 25px;">
    //     <div class="service-box" style="box-shadow:-0.5px 2px 10px 1px rgba(56, 142, 144, 0.17);">
    //         <div class="post-thumbnail"><img src="img/photo1.jpg" alt="" ></div>
    //         <div class="col-12" style="padding-left: 2em;">
    //             <div class="service-tag-title">友善飲食平臺</div>
    //             <div class="service-detail">我們重視每種飲食需求<br>透過營養標籤篩選餐點<br>還能匯入飲食日記、線上訂餐。</div>
    //         </div>
    //     </div>
    // </div>
    ajax('GET', '/api/news/list', {}, function (a) {
        $('#events').empty();
        $('#blog').empty();
        var list = a.data;
        for (var i in list) {
            var news = list[i];
            var div = '' +
                    '<div class="col-12 col-sm-6 col-lg-4" style="padding: 25px;">' +
                        '<div class="news-box">' +
                            '<div class="news-thumbnail" style="background-image: url(/upload/Image/'+ news['image'] + ');"></div> ' +
                            '<div class="col-12" style="padding-left: 2em;">' +
                                '<div class="service-tag-title">' + news['title'] + '</div>' +
                                '<div class="service-detail">' + news['content'].replace('<br>', '').substring(2, 90) + '. . .</div>' +
                            '<button class="btn-pete col-12 pnews" data-id="' + news['id'] + '" style="margin: 30px 0 0 0;">查看更多 <i class="fas fa-long-arrow-alt-right"></i></button>' +
                            '</div>\n' +
                        '</div>' +
                    '</div>';
            if (news['category'] == 1)
                $('#events').append(div);
            else
                $('#blog').append(div);
        }
    })

    $(document).on('click', '.pnews', function () {
        let tid = $(this).data('id');
        console.log(tid);
        window.location.href = '/new?tid=' + tid;
    });

 });